import java.util.*;   //TO know the  operaters in the program.
public class class15_9_1{
    public static void main(String[] args){
        Scanner sc=new Scanner(System.in);
        System.out.println("Enter a value :");
        int a=sc.nextInt();
        String  res =(a%2==0)?"Even":"Odd";                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             
        System.out.println("The value of "+a+" is:"+ res);
    }
}